package com.controller;

import Modelo.Producto;
import ModeloDAO.ProductoDAO;
import ModeloDAO.UnidaddeMedidaDAO;
import com.google.gson.Gson;
import com.modelos.RespuestaJson;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.OutputStream;
import java.io.PrintWriter;
import java.math.BigDecimal;
import java.util.Base64;
import java.util.logging.Level;
import java.util.logging.Logger;
import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

/**
 *
 * @author certus3
 */
public class ProductoController extends HttpServlet {

    /**
     * Processes requests for both HTTP <code>GET</code> and <code>POST</code>
     * methods.
     *
     * @param request servlet request
     * @param response servlet response
     * @throws ServletException if a servlet-specific error occurs
     * @throws IOException if an I/O error occurs
     */
        private Gson json=new Gson();
    private String jsonResponse;
    protected void processRequest(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        
        try {
            
    
                 String action = request.getParameter("accion");
          
        String id_producto = (request.getParameter("id_producto").toString());
        String nombre_producto="" ;
       String descripcion_producto="";
        String lote_producto="" ;
        Integer codigo_presentacion=0;
        Integer codigo_categoria=0;
        String foto_base64="";
        
        if (!(action.equals("consultarProducto")) && !(action.equals("subirfotoProducto")) )
        {
         nombre_producto = request.getParameter("nombre_producto");
        descripcion_producto = request.getParameter("descripcion_producto");
        lote_producto = request.getParameter("lote_producto");
        codigo_presentacion = Integer.parseInt(request.getParameter("codigo_presentacion").toString());
        codigo_categoria = Integer.parseInt(request.getParameter("codigo_categoria").toString());        
        foto_base64 = request.getParameter("foto_base64").toString();        
        }
        
        
//        
//        
        
        RespuestaJson respuesta = new RespuestaJson();
                
     
        Producto mp = new Producto();
        
        switch (action) {
            case "subirfotoProducto":
                
                 
                 foto_base64 = request.getParameter("fotobase64");
                byte[] data = Base64.getDecoder().decode(foto_base64);  
                String ruta;//=getServletContext().getRealPath("/")+"/foto.jpg";
                
                
               // ruta = "/fotos/foto.jpg";
                ruta = "C:\\sCertus\\CursoJSPMaven\\src\\main\\webapp\\fotos\\foto1.jpg";
                OutputStream stream = new FileOutputStream(ruta);
                stream.write(data);

                break;
            case "addProducto":                
                mp.setCodigo(id_producto);
                mp.setNombre(nombre_producto);
                mp.setDescripcion(descripcion_producto);
                mp.setLote(lote_producto);
                mp.setCodigo_categoria(codigo_categoria);
                mp.setCodigo_presentacion(codigo_presentacion);
                mp.setFoto_base64(foto_base64);
                
                ProductoDAO.Registrar(mp);
                respuesta.setEstado("ok");
                respuesta.setMensaje("Transaccion ok");                
                this.jsonResponse = this.json.toJson(respuesta);
                response.setContentType("application/json");
                response.getWriter().write(this.jsonResponse);   
                break;
            case "updateProducto":
                mp.setCodigo(id_producto);
                mp.setNombre(nombre_producto);
                mp.setDescripcion(descripcion_producto);
                mp.setLote(lote_producto);
                mp.setCodigo_categoria(codigo_categoria);
                mp.setCodigo_presentacion(codigo_presentacion);
                mp.setFoto_base64(foto_base64);
                ProductoDAO.Actualizar(mp);
                respuesta.setEstado("ok");
                respuesta.setMensaje("Transaccion ok");
                this.jsonResponse = this.json.toJson(respuesta);
                response.setContentType("application/json");
                response.getWriter().write(this.jsonResponse);
                break;
            case "consultarProducto":
                mp = ProductoDAO.Consultar(id_producto);    
                if (mp!=null)
                {
                respuesta.setEstado("ok");
                respuesta.setMensaje("Transaccion ok");
                }
                else
                {
                mp=new Producto();
                respuesta.setEstado("error");
                respuesta.setMensaje("No existe Producto");
                }
                mp.setRespuestaJson(respuesta);
                this.jsonResponse = this.json.toJson(mp);
                response.setContentType("application/json");
                response.getWriter().write(this.jsonResponse);
                
                break;
            default:
                request.getRequestDispatcher("/jsp_app/mantenimiento/producto.jsp").forward(request, response);
                break;
        }
            } catch (Exception e) {
                Logger.getLogger(e.getMessage().toString());
            
        }
    }

    // <editor-fold defaultstate="collapsed" desc="HttpServlet methods. Click on the + sign on the left to edit the code.">
    /**
     * Handles the HTTP <code>GET</code> method.
     *
     * @param request servlet request
     * @param response servlet response
     * @throws ServletException if a servlet-specific error occurs
     * @throws IOException if an I/O error occurs
     */
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        processRequest(request, response);
    }

    /**
     * Handles the HTTP <code>POST</code> method.
     *
     * @param request servlet request
     * @param response servlet response
     * @throws ServletException if a servlet-specific error occurs
     * @throws IOException if an I/O error occurs
     */
    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        processRequest(request, response);
    }

    /**
     * Returns a short description of the servlet.
     *
     * @return a String containing servlet description
     */
    @Override
    public String getServletInfo() {
        return "Short description";
    }// </editor-fold>

}
